<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Baseauth extends CI_Model{    public $appjson = 'application/json';    public function __construct()    {        parent::__construct();        $this->load->database();    }    public function checkHttpReferer($href='')    {        $base_url = base_url();        if(is_null($href))        {            return false;        }        else if($href==$base_url)        {            return true;        }        else        {            return false;        }    }    public function checkAuthorization($table,$condition='',$select='*')    {        $this->db->select($select, FALSE);        $this->db->from( $this->db->dbprefix($table));        if(!empty($condition))            $this->db->where($condition);        $result = $this->db->get();        $res = $result->result_array();        if(!empty($res))        {            return true;        }        else        {            return false;        }    }}