<?php defined('BASEPATH') OR exit('No direct script access allowed'); ?><md-content class="mylayout-padding" ng-cloak>    <div layout="column">        <div layout-gt-sm="row" layout-xs="column" layout-gt-xs="column" layout="row">            <div flex flex-xs="100">                <h3 class="heading-stylerespond">Deployment Equipments </h3>            </div>            <div ng-if="user_role_code==HMADMIN" flex="5" hide-xs hide-sm> </div>            <md-input-container ng-if="user_role_code==HMADMIN" ng-show="user_role_code==HMADMIN" class="no-margin-padding-md-input" flex="20" flex-xs="100">                <md-select ng-change="UndeployedDevices()" placeholder="Select Branch *" ng-model="deployment_devices.branch_id" aria-label="branch_id">                    <md-option ng-value="branch.BRANCH_ID" ng-repeat="branch in branchs">{{branch.BRANCH_NAME}}</md-option>                </md-select>            </md-input-container>        </div>        <div layout="row" layout-align="center center">            <md-input-container class="md-block" flex-gt-sm flex="20">                <label>PO No.</label>                <input type="search" ng-model="deployment_devices.po_no" name="seqpid" aria-label="seqpid"/>            </md-input-container>            <div flex="5" hide-xs hide-sm>&nbsp;&nbsp;	</div>            <md-input-container class="md-block" flex-gt-sm flex="20">                <label>Serial No.</label>                <input type="search" ng-model="deployment_devices.serial_no" name="seqpid" aria-label="seqpid"/>            </md-input-container>            <div flex="1" hide-xs hide-sm>&nbsp;&nbsp;</div>            <md-button class="md-icon-button md-raised md-primary" ng-click="UndeployedDevices()"  md-theme="default" aria-label="submit">                <ng-md-icon icon="search" style="fill:#fff" size="24"></ng-md-icon>            </md-button>        </div>        <div layout="row">            <table class="md-api-table table table-bordered">                <thead>                <tr>                    <th>Equipment Name</th>                    <th>Equipment Model</th>                    <th>PO No.</th>                    <th>Serial No.</th>                    <th>Vendor</th>                    <th>Contract Type</th>                    <th>Action</th>                </tr>                </thead>                <tbody ng-if="!isEmpty(ud_devices)">                <tr ng-repeat="ud_device in ud_devices">                    <td>{{ud_device.E_NAME}}</td>                    <td>{{ud_device.E_MODEL}}</td>                    <td>{{ud_device.PONO}}</td>                    <td>{{ud_device.ES_NUMBER}}</td>                    <td>{{ud_device.VENDOR_NAME}}</td>                    <td>{{ud_device.AMC_TYPE}}</td>                    <td style="text-align: center;">                        <button class="btn btn-xs btn-default" ng-click="getDeviceDetails(ud_device.ID)" aria-label="Deploy Button{{$index}}">                            <md-tooltip md-direction="top">Deploy</md-tooltip>                            <md-icon class="material-icons-new" style="color:#614DA4">near_me</md-icon>                        </button>                    </td>                </tr>                </tbody>                <tbody ng-else>                <tr>                    <td colspan="7" style="text-align:center">No Devices Found...!</td>                </tr>                </tbody>            </table>        </div>        <div flex layout="row" class="marginb-10" ng-if="no_of_recs!=0">            <div flex-xs="100" flex="20" layout-align="start start" flex layout="column">                <md-button class="md-icon-button md-primary md-raised" aria-label="Total">                    <md-tooltip md-direction="top">Total Records</md-tooltip>                    {{no_of_recs}}                </md-button>            </div>            <div flex="20" hide-xs hide-sm><!-- Space --></div>            <div flex-xs="100" flex="60" layout="column" layout-align="end end">                <cl-paging flex cl-pages="paging.total" , cl-steps="5" , cl-page-changed="UndeployedDevices(paging.current)" , cl-align="end end" , cl-current-page="paging.current"></cl-paging>            </div>        </div>    </div></md-content>