<?php defined('BASEPATH') OR exit('No direct script access allowed'); ?><md-content class="mylayout-padding">    <div flex layout="column">        <div layout-gt-sm="row" layout-xs="column" layout-gt-xs="column" layout="row">            <h3 flex class="heading-stylerespond">Equipment Deployment</h3>        </div>        <form name="DeployDevice" action="javascript:void(0);">            <div layout-gt-sm="row" layout-xs="column" layout-gt-xs="column" layout="row" layout-wrap>                <md-input-container flex="20">                    <label>Equipment ID *</label>                    <md-select required ng-model="deploy_device.eq_condition" name="equp_condition" required>                        <md-option ng-repeat="equipment_type in equipment_deploy_condition" ng-value="equipment_type">{{equipment_type}}</md-option>                    </md-select>                </md-input-container>                <div flex="5" hide-xs hide-sm><!-- Space --></div>                <md-input-container ng-show="deploy_device.eq_condition==equipment_deploy_condition[1]" flex="25">                    <label>Equipment ID</label>                    <input type="text" ng-required="deploy_device.eq_condition==equipment_deploy_condition[1]" ng-model="deploy_device.eqpid" name="rceqpid" aria-label="rc username"/>                </md-input-container>                <div flex="5" ng-show="deploy_device.eq_condition==equipment_deploy_condition[1]" hide-xs hide-sm><!-- Space --></div>                <md-input-container ng-show="deploy_device.eq_condition==equipment_deploy_condition[0]" flex="20">                    <label>Department</label>                    <md-select ng-required="deploy_device.eq_condition==equipment_deploy_condition[0]" ng-model="deploy_device.department"  name="depts">                        <md-option ng-repeat="dept in depts" ng-value="dept.CODE">                            {{dept.USER_DEPT_NAME}}                        </md-option>                    </md-select>                </md-input-container>                <div flex="5" ng-show="deploy_device.eq_condition==equipment_deploy_condition[0]" hide-xs hide-sm><!-- Space --></div>                <md-input-container ng-show="deploy_device.eq_condition==equipment_deploy_condition[0]" flex="20">                    <label>Classifications</label>                    <md-select ng-required="deploy_device.eq_condition==equipment_deploy_condition[0]" ng-model="deploy_device.classification" name="classification">                        <md-option ng-repeat="classification in m_classifications" ng-value="classification.CODE">                            {{classification.RESPONSIBLE_DEPT}} ({{classification.CODE}})                        </md-option>                    </md-select>                </md-input-container>                <div flex="5" ng-show="deploy_device.eq_condition==equipment_deploy_condition[0]" hide-xs hide-sm><!-- Space --></div>                <md-input-container flex="20">                    <label>City</label>                    <input ng-readonly="true" type="text" ng-model="deploy_device.city_name" name="city_code" aria-label="city_code"/>                </md-input-container>                <div flex="5" hide-xs hide-sm><!-- Space --></div>                <md-input-container flex="20">                    <label>Branch</label>                    <input ng-readonly="true" type="text" ng-model="deploy_device.branch_name" name="equp_branch" aria-label="equp_branch"/>                </md-input-container>                <div ng-show="deploy_device.eq_condition==equipment_deploy_condition[0] || deploy_device.eq_condition==undefined" flex="5" hide-xs hide-sm><!-- Space --></div>                <mdp-date-picker mdp-placeholder="Date of Install *"  name="date_of_install" required class="md-block" flex-gt-sm flex="20" mdp-format="DD-MM-YYYY" ng-model="deploy_device.dateof_install">                </mdp-date-picker>                <div flex="5" hide-xs hide-sm><!-- Space --></div>                <md-input-container ng-show="deploy_device.eq_condition==equipment_deploy_condition[0]"  flex="20">                    <label>Equipment Category *</label>                    <md-select required ng-model="deploy_device.E_TYPE" name="equp_type">                        <md-option ng-repeat="equp_name in equp_names" ng-value="equp_name.CODE">                            {{equp_name.NAME}}                        </md-option>                    </md-select>                </md-input-container>            </div>            <div class="row">                <center>                    <md-button class="md-raised md-accent" ng-disabled="DeployDevice.$invalid" ng-click="deployDevice($event,deploy_device)" aria-label="button"> Deploy </md-button>                </center>            </div>        </form>    </div></md-content>