<?php defined('BASEPATH') OR exit('No direct script access allowed'); ?><div layout="column" layout-fill>    <md-toolbar class="md-whiteframe-z1">        <div class="md-toolbar-tools" style="background-color:#604ca3;">            <a ui-sref="{{user_path}}">                <img src="<?= base_url()?>assets/images/hospiasset_logo_final.png">            </a>        </div>    </md-toolbar>    <md-content class="mylayout-padding" ng-cloak>        <div layout="column">            <h3 class="heading-stylerespond">Generate Call</h3>            <div style="margin-top:10px;" flex layout="row" layout-align="center center">                <form method="POST" name="GenerateCallForm" flex="40" flex-xs="100" class="md-whiteframe-3dp mylayout-padding" autocomplete="off">                    <div layout="column" layout-wrap flex>                        <md-input-container class="md-block" flex>                            <label>Employee ID</label>                            <input only-digits="only-digits" type="text" ng-change="getCCCEmpDetls(gen_call.caller_id)" ng-model="gen_call.caller_id" name="caller_id" aria-label="caller_id"/>                        </md-input-container>                        <div layout="row" class="md-block" flex>                            <a style="font-size: 12px;cursor: pointer" ng-click="getMyGeneratedCalls(gen_call.caller_id)">My Calls</a>                        </div>                        <md-input-container class="md-block" flex>                            <label>Name</label>                            <input type="text"  required ng-model="gen_call.user_name" name="user_name" aria-label="user_name"/>                        </md-input-container>                        <md-input-container class="md-block" flex>                            <label>Mobile</label>                            <input type="text"  required ng-model="gen_call.mobile_no" name="mobile_no" aria-label="mobile_no"/>                        </md-input-container>                        <md-input-container class="md-block" flex>                            <label>Email</label>                            <input type="text"  required ng-model="gen_call.email" name="email" aria-label="email"/>                        </md-input-container>                        <md-input-container class="md-block" flex-gt-sm flex="40">                            <label>Select Branch *</label>                            <md-select ng-change="getBranchDevices(gen_call.branch_id)" name="branch_id" required ng-model="gen_call.branch_id" aria-label="branch_id">                                <md-option ng-value="branch.BRANCH_ID" ng-repeat="branch in branchs">{{branch.BRANCH_NAME}}</md-option>                            </md-select>                            <div ng-messages="addUserForm.branch_id.$error">                                <div ng-message="required">Required.</div>                            </div>                        </md-input-container>                        <md-input-container class="md-block" flex>                            <label>Department</label>                            <md-select   ng-change="getDepartmentDevices(gen_call.dept_id)"  ng-disabled="gen_call.branch_id==null" name="dept_id" aria-label="dept_id" ng-model="gen_call.dept_id"/>                            <md-option ng-value="dept.CODE" ng-repeat="dept in depts">{{dept.USER_DEPT_NAME}} ({{dept.CODE}})</md-option>                            </md-select>                            <!-- <input type="text" readonly required ng-model="gen_call.dept_id" name="dept_id" aria-label="dept_id"/>--->                        </md-input-container>                        <md-input-container>                            <label>Equipment ID *</label>                            <md-select required  ng-model="gen_call.device_id" name="device_id">                                <md-option ng-repeat="device in devices"  ng-value="device.E_ID">                                    {{device.E_ID}}                                </md-option>                            </md-select>                        </md-input-container>                        <md-input-container>                            <label>Nature of Call</label>                            <md-select required  ng-model="gen_call.complaint" name="complaint">                                <md-option ng-value="nature_of_calls[0]">{{nature_of_calls[0]}}</md-option>                                <md-option ng-value="nature_of_calls[1]">Non-Schedule Call</md-option>                            </md-select>                        </md-input-container>                        <md-input-container ng-if="gen_call.complaint==nature_of_calls[0]" class="md-block" flex>                            <label>Incident Type *</label>                            <md-select name="roleid" ng-required="gen_call.complaint==nature_of_calls[0]" ng-model="gen_call.itype" aria-label="itype">                                <md-option  ng-value="itype.CODE" ng-repeat="itype in itypes">{{itype.ITYPE}}</md-option>                            </md-select>                        </md-input-container>                        <md-input-container>                            <label>Priority</label>                            <md-select required ng-model="gen_call.priority" name="priority">                                <md-option ng-repeat="device_priority in device_priorities"  ng-value="device_priority.PID">                                    {{device_priority.PNAME}}                                </md-option>                            </md-select>                        </md-input-container>                        <md-input-container class="md-block" flex>                            <label>Remarks</label>                            <input type="text" ng-model="gen_call.generationremarks" name="generationremarks" aria-label="generationremarks"/>                        </md-input-container>                        <div flex layout="row" layout-align="center center">                            <center>                                <md-button class="md-raised md-accent" ng-click="UserCallgenerate()" ng-disabled="GenerateCallForm.$invalid" aria-label="submit">Submit</md-button>                                <md-button class="md-raised md-default" ng-click="gotoLogin()" aria-label="cancel">Cancel</md-button>                            </center>                        </div>                    </div>                </form>            </div>        </div>    </md-content></div>