<?php defined('BASEPATH') OR exit('No direct script access allowed'); ?><style>    tbody    {        display:block;        overflow:auto;    }    thead, tbody tr {        display:table;        width:100%;        table-layout:fixed;    }    thead    {        width: calc( 100% - 1em )    }    table    {        width:100%;    }    .table-bordered > thead > tr > th    {        border: none;    }    .table-bordered    {        border: none;    }    .table-bordered > tbody > tr > td    {        border: 1px solid #fff;        border-top: 0px solid #fff;        padding: 1px 2px 3px 4px;        line-height: 27px;    }</style><md-content class="mylayout-padding" ng-cloak>    <div layout="column">        <div ng-include="'includes/call_alerts'"></div>        <h3 class="heading-stylerespond">{{title}}</h3>        <div layout-gt-sm="row" layout-xs="column" layout-gt-xs="column" layout="row" layout-align="center center" style="margin-top:10px;">            <mdp-date-picker ng-model="unitcall_search.fromdate" mdp-placeholder="From Date" mdp-format="DD-MM-YYYY" flex="15" mdp-max-date="minDate">            </mdp-date-picker>            <div flex="5" hide-xs hide-sm>&nbsp;&nbsp;	</div>            <mdp-date-picker ng-model="unitcall_search.todate" mdp-placeholder="To Date" flex="15"  mdp-format="DD-MM-YYYY" mdp-min-date="calllog_report_search.fromdate">            </mdp-date-picker>            <div flex="1" hide-xs hide-sm>&nbsp;&nbsp;</div>            <md-input-container class="no-margin-padding-md-input" flex="20" flex-xs="100">                <md-select placeholder="Select Branch *" ng-model="unitcall_search.branch_id" ng-change="loadUnitDepartments(unitcall_search.branch_id)" aria-label="user_branch">                    <md-option ng-value="branch.BRANCH_ID" ng-selected="branch.BRANCH_ID == 'All'" ng-repeat="branch in branchs">{{branch.BRANCH_NAME}}</md-option>                </md-select>            </md-input-container>            <md-input-container class="md-block" flex-gt-sm>                <label>Department</label>                <md-select placeholder="Department *" ng-model="unitcall_search.dept_id"  aria-label="user_dept">                    <md-option ng-value="nullValue">                        Select Department                    </md-option>                    <md-option ng-repeat="dept in unit_depts" ng-value="dept.CODE" >                        {{dept.USER_DEPT_NAME}}({{dept.CODE}})                    </md-option>                </md-select>                <div ng-messages="AddDevice.depts.$error">                    <div ng-message="required">Required</div>                </div>            </md-input-container>            <md-button class="md-icon-button md-raised md-accent" ng-click=""  md-theme="default" aria-label="submit">                <ng-md-icon icon="search" style="fill:#fff" size="24" ng-click="getUnitWiseSecCounts(unitcall_search)"></ng-md-icon>            </md-button>        </div>        <div flex layout="column" layout-md="column" flex-gt-sm="column" layout-xs="column" style="margin-top: 10px;">            <table class="md-api-table table table-bordered" ng-cloak style="margin-bottom: 0px;">                <thead>                <tr>                    <th style="width: 16%;">Unit Name</th>                    <th style="width: 7%;">Non Scheduled </th>                    <th style="width: 7%;">Scheduled </th>                    <th style="width: 7%;">In Progress</th>                    <th style="width: 7%;">On Hold</th>                    <th style="width: 7%;">PMS</th>                    <th style="width: 7%;">Callibration</th>                    <th style="width: 7%;">Adverse</th>                    <th style="width: 7%;">Transfer</th>                    <th style="width: 7%;">Condemenation</th>                    <th style="width: 7%;">Contracts Expired</th>                    <th style="width: 7%;">Total</th>                </tr>                </thead>            </table>            <table class="md-api-table table table-bordered" ng-cloak>                <tbody style="height:350px;">                <tr ng-repeat="tc_device in unit_devices">                    <td style="width: 16%;">{{tc_device.branch_name}}</td>                    <td style="width: 7%;text-align: center;">{{tc_device.today_calls_cnt}}</td>                    <td style="width: 7%;text-align: center;">{{tc_device.responded_calls_cnt}}</td>                    <td style="width: 7%;text-align: center;">{{tc_device.attended_calls_cnt}}</td>                    <td style="width: 7%;text-align: center;">{{tc_device.pending_calls_cnt}}</td>                    <td style="width: 7%;text-align: center;">{{tc_device.pending_pms}}</td>                    <td style="width: 7%;text-align: center;">{{tc_device.pending_qc}}</td>                    <td style="width: 7%;text-align: center;">{{tc_device.adverse_incidents_count}}</td>                    <td style="width: 7%;text-align: center;">{{tc_device.transfers_cnt}}</td>                    <td style="width: 7%;text-align: center;">{{tc_device.condemnation_cnt}}</td>                    <td style="width: 7%;text-align: center;">{{tc_device.completed_calls_cnt}}</td>                    <td style="width: 7%;text-align: center;">                        {{tc_device.today_calls_cnt + tc_device.responded_calls_cnt + tc_device.attended_calls_cnt +                        tc_device.pending_calls_cnt + tc_device.pending_pms + tc_device.pending_qc +                        tc_device.adverse_incidents_count + tc_device.transfers_cnt +tc_device.condemnation_cnt                        +tc_device.completed_calls_cnt}}                    </td>                </tr>                </tbody>            </table>        </div>    </div></md-content>