<?php defined('BASEPATH') OR exit('No direct script access allowed'); ?><md-content class="mylayout-padding" ng-cloak>    <div layout="column">        <div layout-gt-sm="row" layout-xs="column" layout-gt-xs="column" layout="row">            <div flex flex-xs="100">                <h3 class="heading-stylerespond">{{title}} </h3>            </div>            <div ng-if="user_role_code==HMADMIN" flex="5" hide-xs hide-sm> </div>            <md-input-container ng-if="user_role_code==HMADMIN" ng-show="user_role_code==HMADMIN" class="no-margin-padding-md-input" flex="20" flex-xs="100">                <md-select placeholder="Select Branch *" ng-model="respondcall_search.branch_id" ng-change="SearchRespondedCalls()" aria-label="branch_id">                    <md-option ng-value="branch.BRANCH_ID" ng-repeat="branch in branchs">{{branch.BRANCH_NAME}}</md-option>                </md-select>            </md-input-container>        </div>        <div layout="row">            <md-datepicker  ng-model="respondcall_search.fromdate" md-placeholder="From Date" md-max-date="maxDate"  flex-gt-sm>            </md-datepicker>            <div flex="1" hide-xs hide-sm>&nbsp;&nbsp;	</div>            <md-datepicker  ng-model="respondcall_search.todate" md-placeholder="To Date" md-max-date="maxDate" flex-gt-sm>            </md-datepicker>            <div flex="1" hide-xs hide-sm>&nbsp;&nbsp;	</div>            <md-input-container  class="md-block" flex-gt-sm>                <label>Department</label>                <md-select ng-model="respondcall_search.department">                    <md-option ng-value="nullValue" >                        Select Department                    </md-option>                    <md-option ng-repeat="dept in depts"  ng-value="dept.CODE" >                        {{dept.USER_DEPT_NAME}}                    </md-option>                </md-select>                <div ng-messages="AddDevice.depts.$error">                    <div ng-message="required">Required</div>                </div>            </md-input-container>            <div flex="1" hide-xs hide-sm>&nbsp;&nbsp;</div>            <md-autocomplete class="md-block" flex-gt-sm                             md-no-cache="false"                             md-selected-item="searched.EID"                             md-search-text="searchEid"                             md-items="item in searchTextChange(searchEid)"                             md-item-text="item.E_ID"                             md-min-length="0"                             md-floating-label="Search Eq. id?">                <md-item-template>                    <span md-highlight-text="searchText" md-highlight-flags="^i">{{item.E_ID}}</span>                </md-item-template>                <md-not-found>                    No Equipment Match Found                </md-not-found>            </md-autocomplete>            <center>                <md-button class="md-icon-button md-raised md-accent" ng-click="SearchRespondedCalls()"  md-theme="default" aria-label="submit">                    <ng-md-icon icon="search" style="fill:#fff" size="24"></ng-md-icon>                </md-button>            </center>        </div>        <div layout="row">            <table class="md-api-table table table-bordered">                <thead>                <tr>                    <th>Equipment ID</th>                    <th>Equipment Name</th>                    <th>Location</th>                    <th>Complaint</th>                    <th>Caller</th>                    <th>Dept</th>                    <th>Contract Type </th>                    <th>Call Date</th>                    <th>Status</th>                    <th ng-if="user_role_code==HBHOD">Remind</th>                </tr>                <!--    <tr>                        <th>Equipment ID</th>                        <th>Location</th>                        <th>Raised By</th>                        <th>Complaint</th>                        <th>Dept</th>                        <th>Type </th>                        <th>Call Date</th>                        <th>Status</th>                        <th ng-if="user_role_code==HBHOD">Remind</th>                        <th>Attend</th>                        <th>Pending</th>                        <th>Complete</th>                    </tr>-->                </thead>                <tbody ng-if="rc_devices!=null">                <tr ng-repeat="rc_device in rc_devices">                    <td>{{rc_device.EID}}</td>                    <td>{{rc_device.eq_name}}</td>                    <td>{{rc_device.location}}</td>                    <td>{{rc_device.NATURE_OF_COMP}}</td>                    <td>{{rc_device.CALLER_UNAME}}</td>                    <td>{{rc_device.DEPT_NAME}}</td>                    <td>{{rc_device.TYPE}}</td>                    <td>{{rc_device.CDATETIME+'000' | date:'dd-MM-yyyy hh:mm a'}}</td>                    <td>{{rc_device.STATUS}}</td>                    <td ng-if="user_role_code==HBHOD">                        <md-button class="md-icon-button my-md-icon-button md-raised md-default" ng-click="RemindtoBME(rc_device,call_alert_type[0])" aria-label="Conduct Button{{$index}}">                            <md-tooltip md-direction="top">Remind</md-tooltip>                            <i class="fa fa-bell-o" aria-hidden="true" style="color:#ffa076"></i>                        </md-button>                    </td>                </tr>                </tbody>                <tbody ng-else>                <tr>                    <td colspan="9" style="text-align:center">No calls Found...!</td>                </tr>                </tbody>            </table>        </div>    </div></md-content>