<?php defined('BASEPATH') OR exit('No direct script access allowed');?><md-content class="mylayout-padding" md-theme="hospiclr" ng-cloak>    <div layout="column">        <h3 class="heading-stylerespond">Assign Round</h3>        <span flex class="mandatory-fileds">Mandatory Fields are Marked with an asterisk(*)</span>       <!-- <span flex class="mandatory-fileds">{{round_assign}}</span>-->        <div flex layout="row" layout-align="center center">            <form method="POST" name="RoundsassignForm" flex="50" class="md-whiteframe-1dp mylayout-padding" autocomplete="off">                <div flex layout="row">                    <md-input-container class="md-block" flex-gt-sm flex="40">                        <label>Type *</label>                        <md-select required ng-model="round_assign.status" name="status">                            <md-option ng-if="rounds_status[0]==rstatus" ng-repeat="rstatus in rounds_status" ng-value="rstatus">                                <span style="text-transform:capitalize;">{{rstatus}}</span>                            </md-option>                        </md-select>                        <div class="md-errors-spacer"></div>                        <div ng-messages="RoundsassignForm.status.$error" md-auto-hide="true">                            <div ng-message="required">Required</div>                        </div>                    </md-input-container>                    <div flex="20" hide-xs hide-sm><!-- Space --></div>                    <md-input-container class="md-block" flex-gt-sm flex="40">                        <label>Assign From *</label>                        <md-select ng-model="round_assign.assignfrom" name="assign_from" required>                            <md-option ng-value="user_id">                                {{user_name}}                            </md-option>                        </md-select>                        <div class="md-errors-spacer"></div>                        <div ng-messages="RoundsassignForm.assignfrom.$error" md-auto-hide="true">                            <div ng-message="required">Required</div>                        </div>                    </md-input-container>                </div>                <div flex layout="row">                    <md-input-container flex="40">                        <label>Assign to *</label>                        <md-select ng-model="round_assign.assign_to" name="assign_to" required name="assign_to">                            <md-option ng-repeat="bmehod in bmehods" ng-value="bmehod.USER_ID">                                {{bmehod.USER_NAME}}                            </md-option>                        </md-select>                    </md-input-container>                    <div flex="20" hide-xs hide-sm><!-- Space --></div>                    <md-input-container flex="40">                        <label>Department *</label>                        <md-select required multiple ng-model="round_assign.depts" md-on-open="getRoundsDepts(round_assign)" name="depts" ng-disabled="round_assign.assign_to==null">                            <md-option ng-repeat="dept in depts"  ng-value="dept.CODE">                                {{dept.USER_DEPT_NAME}} ({{dept.CODE}})                            </md-option>                        </md-select>                    </md-input-container>                </div>                <div layout="row">                    <mdp-date-picker mdp-placeholder="Date *" class="md-block" flex-gt-sm mdp-format="DD/MM/YYYY" required ng-model="round_assign.current_date" mdp-min-date="maxDate" ></mdp-date-picker>                    <div flex="20" hide-xs hide-sm><!-- Space --></div>                    <md-input-container class="md-block" flex="40">                        <label>Remarks</label>                        <input ng-model="round_assign.remarks">                    </md-input-container>                </div>                <div flex layout="row" layout-align="center center">                    <center>                        <md-button class="md-raised md-accent" ng-click="RoundAssign(round_assign)" ng-disabled="RoundsassignForm.$invalid" aria-label="submit">Submit</md-button>                    </center>                    <center>                        <md-button class="md-raised md-default" ui-sref="home.{{user_role_code | lowercase}}_rounds_complete" aria-label="cancel">Cancel</md-button>                    </center>                </div>            </form>        </div>    </div></md-content>