<?php defined('BASEPATH') OR exit('No direct script access allowed'); ?><md-content class="mylayout-padding" ng-cloak>    <div layout="column">        <div ng-include="'includes/call_alerts'"></div>        <h3 class="heading-stylerespond">{{title}}</h3>        <div layout="row" layout-md="row" flex-gt-sm="row" layout-xs="column" class="widget-container">            <table class="md-api-table table table-bordered" fixed-header ng-cloak style="width:100%;margin-bottom: 5px;">                <thead>                <tr>                    <th></th>                    <th>Equipment ID</th>                    <th>Eq. Name</th>                    <th>Location</th>                    <th>Raised By</th>                    <th>Complaint</th>                    <th>Dept</th>                    <th>Type </th>                    <th>Call Date</th>                    <th>Status</th>					<th>Assigned</th>                    <th>Attend By</th>                    <th>On Hold By</th>                    <th>Completed By</th>                </tr>                </thead>                <tbody ng-if="round_assigneds!=null">                <tr ng-if="admin_call_selected!=admin_calls_select[0]" ng-repeat="round_assigned in round_assigneds">                    <td style="background-color:#c62828;width: 2%;"></td>                    <td style="width: 16%;">-</td>                    <td style="width: 8%;">-</td>                    <td style="width: 8%;">-</td>                    <td style="width: 8%;">{{round_assigned.assigned_by}}</td>                    <td style="width: 8%;">Obervation</td>                    <td style="width: 3%;">{{round_assigned.DEPT_ID}}</td>                    <td style="width: 4%;">Round</td>                    <td style="width: 9%;">{{round_assigned.ROUND_DATE | date:'dd-MM-yyyy'}}</td>                    <td style="width: 4%;">                        <div ng-if="round_assigned.sround!=''">                            Started                        </div>                        <div ng-else>Open</div>                    </td>                    <td style="width: 8%;">{{round_assigned.ROUND_DATE}} / {{round_assigned.assigned_to}}</td>                    <td style="width: 8%;" class="text-center">                        <button ng-click="StartRound($event,round_assigned)" class="btn btn-xs btn-default" ng-disabled="round_assigneds_started==yesstate" aria-label="link">                            <md-tooltip md-direction="top">Start</md-tooltip>                            <md-icon class="material-icons-new" style="color:rgb(68,138,255)">call_received</md-icon>                        </button>                    </td>                    <td style="width: 8%;">-</td>                    <td style="width: 6%;" class="text-center">                        <button ng-click="RoundSubmit1($event,round_assigned)" ng-disabled="round_assigned.sround==''" class="btn btn-xs btn-default" aria-label="link">                            <md-tooltip md-direction="top">Submit</md-tooltip>                            <md-icon class="material-icons-new" style="color:green">done_all</md-icon>                        </button>                    </td>                </tr>                </tbody>                <tbody ng-else>                <tr>                    <td colspan="14" style="text-align:center;width:100%;">No Calls Found...!</td>                </tr>                </tbody>            </table>        </div>        <div flex layout="row" class="marginb-10">            <div flex-xs="100" flex="20" layout-align="start start" flex layout="column">                <md-button class="md-icon-button md-primary md-raised" aria-label="Total">                    <md-tooltip md-direction="top">Total Records</md-tooltip>                    {{no_of_recs}}                </md-button>            </div>            <div flex="20" hide-xs hide-sm><!-- Space --></div>            <div flex-xs="100" flex="60" layout="column" layout-align="end end">                <cl-paging flex cl-pages="paging.total" , cl-steps="5" , cl-page-changed="loadRoundAssigned(paging.current)" , cl-align="end end" , cl-current-page="paging.current"></cl-paging>            </div>        </div>    </div></md-content>